<?php include 'var.php'; if(session_is_registered('userid')){  if(isset($_GET['s'])) $status = $_GET['s'];  if(isset($_POST['status'])){   $statusa = $_POST['s'];   for($i=0;$i<3;$i++){    if($statusa[$i]) $status.= '1';    else $status.= '0';   }   $status = bindec($status);  }  if(isset($status)){   if(!session_is_registered('msg_status')) session_register('msg_status');   $_SESSION['msg_status'] = $status;  }  else{   if(session_is_registered('msg_status')) $status = $_SESSION['msg_status'];   else $status = 6;  }  if(isset($_POST['t'])){   $time = $_POST['t'];   if(!session_is_registered('msg_time')) session_register('msg_time');   $_SESSION['msg_time'] = $time;  }  else{   if(session_is_registered('msg_time')) $time = $_SESSION['msg_time'];   else $time = 0;  }  if(isset($_POST['n'])){   $number = $_POST['n'];   if(!session_is_registered('msg_number')) session_register('msg_number');   $_SESSION['msg_number'] = $number;  }  else{   if(session_is_registered('msg_number')) $number = $_SESSION['msg_number'];   else $number = 5;  }  $html_titel[] = 'Personal Messaging';  $html_headline[] = 'Personal Messaging';  if($status&4){   $v['%statusn%'] = $status&3;   $v['%symboln%'] = htmlformat_symbol('r','gelesene anzeigen');   $v['%check0%'] = ' checked';  }  else{   $v['%statusn%'] = $status|4;   $v['%symboln%'] = htmlformat_symbol('ur','nur ungelesene','.png');  }  if($status&1){   $v['%statusd%'] = 6&$status|2;   $v['%symbold%'] = '&uarr;';   $v['%check2%'] = ' checked';  }  else{   $v['%statusd%'] = 1|$status&5;   $v['%symbold%'] = '&darr;';  }  if($status&2) $v['%check1%'] = ' checked';  $v['%t%'] = $time;  $v['%n%'] = $number;  $v['%msgs%'] = htmlformat_msgs(get_msgs($_SESSION['userid'],$status,$time,$number));  $inhalt = get_face('index.html',$v); }else $inhalt = get_anmeldung_link(); echo face($inhalt);?>